<node TEXT="public void div3ScanSort(int[] items, int start, int end) {&#xa;    if (start &lt; end) {&#xa;        int pivot = items[start];&#xa;        int i = start, j = end, k = start + 1;&#xa;        while (k &lt;= j) {&#xa;            if (items[k] &lt; pivot) {&#xa;                swap(items, i, k);&#xa;                i++;&#xa;                k++;&#xa;            } else if (items[k] &gt; pivot) {&#xa;                swap(items, j, k);&#xa;                j--;&#xa;            } else {&#xa;                k++;&#xa;            }&#xa;        }&#xa;        div3ScanSort(items, start, i - 1);&#xa;        div3ScanSort(items, j + 1, end);&#xa;    }&#xa;}" ID="ID_541817781" CREATED="1531380316645" MODIFIED="1531380317501"/>
