<node TEXT="public void div3DeScanSort(int[] items, int start, int end) {&#xa;    if (start &lt; end) {&#xa;        int pivot = items[start];&#xa;        int i = start, j = end, k = start + 1;&#xa;&#xa;        OUT_LOOP: while (k &lt;= j) {&#xa;            if (items[k] &lt; pivot) {&#xa;                swap(items, i, k);&#xa;                i++;&#xa;                k++;&#xa;            } else if (items[k] == pivot) {&#xa;                k++;&#xa;            } else {&#xa;                // j&#x5411;&#x5de6;&#x626b;&#x63cf;&#xff0c;&#x76f4;&#x5230;&#x4e00;&#x4e2a;&#x4e0d;&#x5927;&#x4e8e;pivot&#x7684;&#x5143;&#x7d20;&#xa;                while (items[j] &gt; pivot) {&#xa;                    j--;&#xa;                    if (k &gt; j) {&#xa;                        // &#x540e;&#x9762;&#x7684;&#x5f85;&#x6392;&#x5143;&#x7d20;&#x5168;&#x5927;&#x4e8e;pivot&#xff0c;&#x76f4;&#x63a5;&#x7ed3;&#x675f;&#x6392;&#x5e8f;&#xa;                        break OUT_LOOP;&#xa;                    }&#xa;                }&#xa;                if (items[j] &lt; pivot) {&#xa;                    swap(items, j, k);&#xa;                    swap(items, i, k);&#xa;                    i++;&#xa;                } else {&#xa;                    swap(items, j, k);&#xa;                }&#xa;                k++;&#xa;                j--;&#xa;            }&#xa;        }&#xa;        div3DeScanSort(items, start, i - 1);&#xa;        div3DeScanSort(items, j + 1, end);&#xa;    }&#xa;}" ID="ID_855281061" CREATED="1531380525333" MODIFIED="1531380526435">
<node TEXT="def quicksort3way(arr,start,end):&#xa;    if start&lt;0 or end&lt;0 or start&gt;=end:return&#xa;    i=start;j=end;k=start+1&#xa;    pivot=arr[start]&#xa;    while k&lt;=j:&#xa;        if arr[k]&lt;pivot:&#xa;            arr[i],arr[k]=arr[k],arr[i]&#xa;            i+=1&#xa;            k+=1&#xa;        elif arr[k]==pivot:&#xa;            k+=1&#xa;        else:&#xa;            while arr[j]&gt;pivot:&#xa;                j-=1&#xa;                if j&lt;k:break&#xa;            if j&lt;k:break&#xa;&#xa;            arr[k],arr[j]=arr[j],arr[k]&#xa;            j-=1&#xa;            if arr[k]&lt;pivot:&#xa;                arr[i],arr[k]=arr[k],arr[i]&#xa;                i+=1&#xa;            k+=1&#xa;    quicksort3way(arr,start,i-1)&#xa;    quicksort3way(arr,j+1,end)" ID="ID_1991628784" CREATED="1531642235460" MODIFIED="1531642417245"/>
