<node TEXT="public void dualPivotQuickSort(int[] items, int start, int end) {&#xa;    if (start &lt; end) {&#xa;        if (items[start] &gt; items[end]) {&#xa;            swap(items, start, end);&#xa;        }&#xa;        int pivot1 = items[start], pivot2 = items[end];&#xa;        int i = start, j = end, k = start + 1;&#xa;        OUT_LOOP: while (k &lt; j) {&#xa;            if (items[k] &lt; pivot1) {&#xa;                swap(items, ++i, k++);&#xa;            } else if (items[k] &lt;= pivot2) {&#xa;                k++;&#xa;            } else {&#xa;                while (items[--j] &gt; pivot2) {&#xa;                    if (j &lt;= k) {&#xa;                        // &#x626b;&#x63cf;&#x7ec8;&#x6b62;&#xa;                        break OUT_LOOP;&#xa;                    }&#xa;                }&#xa;&#xa;                if (items[j] &lt; pivot1) {&#xa;                    swap(items, j, k);&#xa;                    swap(items, ++i, k);&#xa;                } else {&#xa;                    swap(items, j, k);&#xa;                }&#xa;                k++;&#xa;            }&#xa;        }&#xa;        swap(items, start, i);&#xa;        swap(items, end, j);&#xa;&#xa;        dualPivotQuickSort(items, start, i - 1);&#xa;        dualPivotQuickSort(items, i + 1, j - 1);&#xa;        dualPivotQuickSort(items, j + 1, end);&#xa;    }&#xa;}" ID="ID_358370507" CREATED="1531381035291" MODIFIED="1531381036418">
<node TEXT="def dualpartition(arr,start,end,index1,index2):&#xa;    i = start;j = end;k = start + 1&#xa;    if arr[index1]&gt;arr[index2]:arr[index1],arr[index2]=arr[index2],arr[index1]&#xa;    arr[index1],arr[start]=arr[start],arr[index1]&#xa;    arr[index2], arr[end] = arr[end], arr[index2]&#xa;    pivot1 = arr[start]&#xa;    pivot2 = arr[end]&#xa;    print(pivot1,pivot2)&#xa;    while k &lt;= j:&#xa;        if arr[k] &lt; pivot1:&#xa;            arr[i], arr[k] = arr[k], arr[i]&#xa;            i += 1&#xa;            k += 1&#xa;        elif arr[k] &lt;= pivot2 and arr[k]&gt;=pivot1:&#xa;            k += 1&#xa;        else:&#xa;            while arr[j] &gt; pivot2:&#xa;                j -= 1&#xa;                if j &lt; k: break&#xa;            if j &lt; k: break&#xa;&#xa;            arr[k], arr[j] = arr[j], arr[k]&#xa;            j -= 1&#xa;            if arr[k] &lt; pivot1:&#xa;                arr[i], arr[k] = arr[k], arr[i]&#xa;                i += 1&#xa;            k += 1&#xa;    return i,j" ID="ID_696000457" CREATED="1531643383203" MODIFIED="1531643385031"/>
