<node TEXT="bool search(string word) {&#xa;        if (word.size() == 0)&#xa;            return false;&#xa;        TrieNode *nptr = root;&#xa;        for (int i = 0; i&lt;word.size(); i++){&#xa;            if (nptr-&gt;child[word[i] - &apos;a&apos;] == NULL)&#xa;                return false;&#xa;            nptr = nptr-&gt;child[word[i] - &apos;a&apos;];&#xa;        }&#xa;        //&#x5224;&#x65ad;&#x662f;&#x5426;&#x6709;&#x4ee5;&#x5f53;&#x524d;&#x8282;&#x70b9;&#x4e3a;&#x7ed3;&#x5c3e;&#x7684;&#x5b57;&#x7b26;&#x4e32;&#xa;        return nptr-&gt;isWord;&#xa;    }" ID="ID_704870331" CREATED="1531657636305" MODIFIED="1531657637253"/>
</node>
